package entity;import java.util.HashMap;import java.util.Iterator;import java.util.Map;import java.util.Set;//购物车类public class Cart {	private HashMap<Items,Integer> goods;	private double totalprice;		public Cart(){		goods = new HashMap<Items,Integer> ();		totalprice = 0.0;	}		public HashMap<Items, Integer> getGoods() {		return goods;	}	public void setGoods(HashMap<Items, Integer> goods) {		this.goods = goods;	}	public double getTotalprice() {		return totalprice;	}	public void setTotalprice(double totalprice) {		this.totalprice = totalprice;	}		//添加商品进入购物车	public boolean addGoodsInCart(Items item, int number){		if(goods.containsKey(item)){			goods.put(item, goods.get(item)+ number);		}else		{			goods.put(item, number);		}		calTotalPrice();		return true;	}		public boolean removeGoodsFromCart(Items item){		goods.remove(item);		calTotalPrice();		return true;	}		public void calTotalPrice(){		double sum = 0.0;		Set<Items> keys = goods.keySet();//获得Map中的键集合		Iterator<Items> it = keys.iterator();//获得迭代器对象		while(it.hasNext()){			Items i = it.next();			sum += goods.get(i) * i.getPrice();		}		this.setTotalprice(sum);	}	/**	 * @param args	 */	public static void main(String[] args) {		// TODO Auto-generated method stub		Items i1 = new Items(1,"沃特篮球鞋","温州",200,500,"001.jpg");		Items i2 = new Items(2,"李宁运动鞋","广州",300,500,"002.jpg");		Items i3 = new Items(1,"沃特篮球鞋","温州",200,500,"001.jpg");		Cart c = new Cart();		c.addGoodsInCart(i1, 1);		System.out.println("总金额：" + c.getTotalprice());		c.addGoodsInCart(i2, 2);		System.out.println("总金额：" + c.getTotalprice());		c.addGoodsInCart(i3, 3);		System.out.println("总金额：" + c.getTotalprice());				//输出购物车集合		Set<Map.Entry<Items, Integer>> items = c.getGoods().entrySet();		for(Map.Entry<Items, Integer> m:items){			System.out.println(m);		}			}}