package util;public class listHelper {	public static String trim(String list){		String[] items = list.split(",");		int length = 0;		for(int i=0;i<5;i++){			length += items[i].length();		}		length += 5;		list = list.substring(0, length);		return list;	}		public static boolean isContained(String list,int id){		String[] items = list.split(",");		String idstr = Integer.toString(id);		for(int i=0;i<items.length;i++){			if(items[i].equals(idstr)){				return true;			}		}		return false;	}}